@@preprocessing
<% 
// Read variables
var w_id = getvar('world_status.selected_world_id');
var m_m = getvar('world_status.mode_femdom');
var m_c = getvar('world_status.mode_corruption');
var m_f = getvar('world_status.mode_focus');
var u_d = getvar('world_status.user_dingzhen');

// Default to World 1 if not set or invalid
if (!w_id) w_id = 1;

if (w_id == 1) { %>【世界:魔法少女】<% } 
if (w_id == 2) { %>【世界:修仙】<% } 
if (w_id == 3) { %>【世界:战争】<% } 
if (w_id == 4) { %>【世界:都市】<% } 
if (w_id == 5) { %>【世界:堕落】<% } 
if (w_id == 6) { %>【世界:妖怪】<% } 
if (w_id == 7) { %>【世界:欺诈】<% } 
if (w_id == 8) { %>【世界:童话】<% } 
if (w_id == 9) { %>【世界:学园】<% } 

if (m_m == true) { %>【模式:抖M】<% } 
if (m_c == true) { %>【模式:恶堕】<% } 
if (m_f == true) { %>【模式:专注】<% } 
if (u_d == true) { %>【用户:丁真】<% } 
%>

